package chapter3_1;

import java.util.Scanner;

public class ex5 {

	static int binSearchX(int []a,int n,int key) {
		int pl=0; //맨앞인덱스
		int pr=n-1;//검색범위맨뒤의 index
		int count;
		do {
			int pc=(pl+pr)/2; //중앙 인덱스
			if(a[pc]==key)
			{
				//초기값pc=0
				for (pl=0; pc > pl; pc--) // key와 같은 맨 앞의 요소를 찾습니다
					if (a[pc - 1] < key)
						break;
				return pc; // 검색성공
			}
			else if(a[pc]<key)
				pl=pc+1;
			else
				pr=pc-1;
		}while(pl<=pr);
		return -1;
	}
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner s = new Scanner(System.in);

		System.out.print("요솟수：");
		int num = s.nextInt();
		int[] x = new int[num]; // 요솟수 num인 배열

		System.out.println("오름차순으로 입력하세요.");

		System.out.print("x[0]："); // 맨 앞 요소를 읽어 들임
		x[0] = s.nextInt();

		for (int i = 1; i < num; i++) {
			do {
				System.out.print("x[" + i + "]：");
				x[i] = s.nextInt();
			} while (x[i] < x[i - 1]); // 하나 앞의 요소보다 작으면 다시 입력
		}

		System.out.print("찾는 값："); // 키 값을 입력 받음
		int key = s.nextInt();

		int idx = binSearchX(x, num, key); // 배열 x에서 값이 ky인 요소를 검색

		if (idx == -1)
			System.out.println("그 값의 요소가 없습니다.");
		else
			System.out.println(key + "은 x[" + idx + "]에 있습니다.");
	
	}

}
